import React, { Component } from "react";
import "./App.css";

import UserCard from "./components/UserCard";
import Followers from "./components/Followers";


class App extends React.Component {
  constructor() {
    super()
    this.state = {
      userData: [],
      followers: []
    }

  }

  componentDidMount() {
    this.fetchUser();
  }

  handleChange = () => {

  }

  fetchUser = () => {
    const urls = [
      'https://api.github.com/users/justinelai',
      'https://api.github.com/users/justinelai/followers'
    ]
    Promise.all(urls.map(url => fetch(url)
      .then(res => res.json())
      .catch(err => console.error('Request failed', err))
    ))
      .then(data => this.setState({
        userData: data[0],
        followers: data[1]
      }))

  }



  render() {
    return (
      <>
        <UserCardList>
          <Card key={this.state.userData.login} {...this.state.userData} />
          <Subtitle>
            <p>Pssst! Did you know that {this.state.userData.login} has {this.state.userData.followers} followers?</p>
            <p>➠ ➢ ⇨ → ➡</p>
            <p> ➤ ➼ ➡️</p>
          </Subtitle>
          <Followers followers={this.state.followers}/>
        </UserCardList>
      </>
    );
  }

}

export default App;
