import React, { Component } from "react";
import Loader from "./Loader";

export default class Followers extends Component {
  constructor() {
    super();

    this.state = {
      login: "",
      name: "",
      avatar_url: "",
      company: "",
      location: "",
      public_repos: "0",
      followers: "0",
      gists: "0",
      error: null,
      isLoading: true
    };
  }

  componentWillMount() {
    const url = "https://api.github.com/users/lisaMTayl/followers_url";
    fetch(url)
      .then(results => {
        if (results.ok) return results.json();
        else throw new Error("User not found!");
      })
      .then(data => {
        this.setState({
          login: data.login,
          avatar_url: data.avatar_url,
          location: data.location,
          public_repos: data.public_repos,
          followers: data.followers,
          isLoading: false
        });
      })
      .catch(error => {
        this.setState({ error, isLoading: false }, () =>
          console.log("callb", error)
        );
      });
  }

  render() {
    const { error, isLoading } = this.state;

    if (error) {
      return (
        <p
          style={{
            fontSize: "8vh",
            fontWeight: "300",
            margin: "25vh auto",
            color: "red"
          }}
        >
          {error.message}
        </p>
      );
    }
    if (isLoading) return <Loader />;

    return (
      <div classname="card-container">
        <div className="card-header">
          <img src={this.state.avatar_url} className="img-custom" circle />
        </div><
        div className="card-body">
        <h3>{this.state.login}</h3>

        <div>
          <thead>
          <tr>
            <th>REPOS</th>
            <th>GISTS</th>
            <th>FOLLOWERS</th>
          </tr>
          </thead>
          <tbody>
          <tr>
            <td>{this.state.public_repos}</td>
            <td>{this.state.gists}</td>
            <td>{this.state.followers}</td>
          </tr>
          </tbody>
        </div>
      </div>
      </div>
    );
  }
}
