import React, { Component } from "react";
import "../src/App.css";
import SearchForm from "./components/SearchForm";
import UserCard from "./components/UserCard";
import FollowerList from "./components/FollowerList";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userInfo: [],
      followers: []
    };

  }

  componentDidMount() {
    this.fetchDoggos()
  }

  handleDoggoChange = e => {
    this.setState({ dogBreed: e.target.value })
  }

  handleSubmit = e => {
    e.preventDefault()
    this.fetchDoggos()
  }

  fetchDoggos = () => {
    fetch(`https://dog.ceo/api/breed/${this.state.dogBreed}/images`)
      .then(response => {
        console.log("response", response)

        return response.json()
      })
      .then(response => {
        if (response.status === 404) this.setState({ doggos: [] })
        else this.setState({ doggos: response.message })
      })
      .catch(err => {
        console.log(err)
        this.setState({ doggos: [] })
        this.setState({ errorMessage: "no dogs of that type found" })
      })
  }
  render() {

    return (
      <div className="App">
        <UserCard />
        {/*<FollowerList*/}
        {/*  onChange={this.handleChange}*/}
        {/*  onClick={this.handleSubmit}*/}
        {/*  value={searchQuery}*/}
        {/*/>*/}
        {/*{this.state.didSubmit && <UserCard username={searchQuery} />}*/}
      </div>
    );
  }
}

export default App;
