import React, { Component } from "react";
import "./App.css";

import UserCard from "./components/UserCard";
import Followers from "./components/Followers";

import Container from "@material-ui/core/Container";


class App extends React.Component {
  constructor() {
    super()
    this.state = {
      user: [],
      followers: []
    }

  }

  componentDidMount() {
    this.fetchUser();
  }

  handleChange = (e) => {
    this.fetchUser();
    this.fetchFollowers();
  };

  fetchUser = () => {
    fetch(`https://api.github.com/users/lisaMTayl`)
      .then(response => {
        // first promise resolution is used to format the data.
        return response.json();
      })
      .then(userData => this.setState({ user: userData.data}))
      .catch(err => {
        console.log(err);
        // HANDLE THIS ERROR
      });
  };

  fetchFollowers = () => {
    fetch(`https://api.github.com/users/lisaMTayl/followers`)
      .then( response => {
        // format data
        return response.json();
      })
      .then(followerData => this.setState({ user: followerData.data}))
      .catch(err => {
        console.log(err);
        // HANDLE THIS ERROR
      });
  };


  render() {
    return (
      <Container maxWidth="sm" alignItems="center">

        <h2>{this.state.name}</h2>
        <p>{this.state.location}</p>
        <UserCard key={this.state.login} {...this.state.user}/>
        <Followers followers={this.state.followers}/>
      </Container>
    );
  }

}

export default App;
