import React, { Component } from "react";
import "./App.css";

import UserCard from "./components/UserCard";
import Followers from "./components/Followers";


class App extends React.Component {
  constructor() {
    super()
    this.state = {
      userData: [],
      followers: []
    }

  }

  componentDidMount() {
    this.fetchUser();
  }

  handleChange = () => {

  };

  fetchUser = () => {
    const urls = [
      'https://api.github.com/users/lisaMTayl',
      'https://api.github.com/users/lisaMTayl/followers'
    ]
    Promise.all(urls.map(url => fetch(url)
      .then(res => res.json())
      .catch(err => console.error('Request failed', err))
    ))
      .then(data => this.setState({
        user: data[0],
        followers: data[1]
      }))

  };



  render() {
    return (
      // use fragment instead of cluttering up divs
      <>

        <UserCard key={this.state.login}
                  login={this.state.login}
                  name={this.state.name}
                  userUrl={this.state.html_url}
                  avatar={this.state.avatar_url}
                  />


        <Followers followers={this.state.followers}/>
      </>
    );
  }

}

export default App;
