import React from 'react';
import './App.css';
import FollowerList from './components/FollowerList'
import UserCard from './components/UserCard'




class App extends React.Component {
  constructor() {
    super()
    this.state = {
      userData: [],
      followers: []
    }

  }

  componentDidMount() {
    this.fetchUser();
  }

  handleChange = () => {

  }

  fetchUser = () => {
    const urls = [
      'https://api.github.com/users/lisaMTayl',
      'https://api.github.com/users/lisaMTayl/followers'
    ]
    Promise.all(urls.map(url => fetch(url)
      .then(res => res.json())
      .catch(err => console.error('Request failed', err))
    ))
      .then(data => this.setState({
        userData: data[0],
        followers: data[1]
      }))

  }



  render() {
    return (
      <>
        <div>
          <UserCard />
          <div>
            <p>Pssst! Did you know that {this.state.userData.login} has {this.state.userData.followers} followers?</p>
            <p>➠ ➢ ⇨ → ➡</p>
            <p> ➤ ➼ ➡️</p>
          </div>
          <div followers={this.state.followers}/>
        </div>
      </>
    );
  }

}

export default App;
