import React, { Component } from "react";
import "./App.css";

import UserCard from "./components/UserCard";
import Followers from "./components/Followers";


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: [],
      followers: []
    };

  }

  fetchUser = () => {
    fetch(`https://api.github.com/users/lisaMTayl`)
      .then(response => {
        // first promise to format the data
        return response.json();
      })
      .then(data => (user: data ))
  }

  render() {

    return (
      <div>
        <UserCard/>
        <Followers/>
      </div>
    );
  }
}

export default App;
