import React, { Component } from "react";
import "./App.css";

import UserCard from "./components/UserCard";
import Followers from "./components/Followers";


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: [],
      followers: []
    };

  }

  fetchUser = () => {

    fetch(`https://api.github.com/users/lisaMTayl`)
      .then(response => {
        // first promise to format the data
        return response.json();
      })
      .then(userData => this.setState({ user: userData }))
      .catch(err => {
        console.log("Error:  User data not received", err);
        // HANDLE THIS ERROR
      });
  };
  render() {

    return (
      <div>
        {this.state.user.map(e => {
          return (
            <UserCard/>
          )
        })}


      </div>
    );
  }
}

export default App;
